startingFloor = 0

def apartmentInstructions(floor):
    position = 0
    for instruction in instructions:
        if instruction=="(":
            floor+=1
            position+=1
            if floor == -1:
                print(f"Santa will be in the basement at position {position}.")
        elif instruction==")":
            floor-=1
            position+=1
            if floor == -1:
                print(f"Santa will be in the basement at position {position}.")
    print(f"The instructions take Santa to floor {floor}.")
    
